import React, { useState } from "react";
import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Typography,
} from "@mui/material";
import { AiOutlinePlus, AiOutlineMinus } from "react-icons/ai";

export default function QuestionAccordian({ question, answer }) {
  const [expandedAnswer, setExpandedAnswer] = useState(false);
  const handleChange = (panel) => (event, isExpanded) => {
    setExpandedAnswer(isExpanded ? panel : false);
  };
  return (
    <>
      <Accordion
        elevation={2}
        sx={{
          mb: 2,
        }}
        expandedAnswer={expandedAnswer === "panel1"}
        onChange={handleChange("panel1")}
      >
        <AccordionSummary expandIcon={
            expandedAnswer ? (<AiOutlineMinus color="#ca7c96"/>) : (<AiOutlinePlus />)
        }
        aria-controls="panel1a-content"
          id="panel1a-header"
        >
            <Typography variant="h6">
                {question}
            </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography fontSize="0.8rem">{answer}</Typography>
        </AccordionDetails>
      </Accordion>
    </>
  );
}
