import React, {useState} from 'react';
import birthdayData from './data/basic_data';
import List from './components/list';
import { Button, Grid, Box, Container, Typography } from '@mui/material';


function App() {

  const [people, setPeople] = useState(birthdayData)
  return (
    <Grid container 
    justify = "center" 
    alignItems="center"
    spacing={0}
    direction="column"
    >
      <Grid
        display="flex"
        justifyContent="stretch"
        alignItems="center"
        minHeight="90vh"
        minWidth="40vw"
        bgcolor="white"
        flexDirection="column"
        borderRadius="10px"
        boxShadow="5px 5px 5px 5px #888888"
        margin={2}

      >
        <Typography sx={{ align: "center" }} variant="h3" component="h3" gutterBottom>{people.length} birthdays today</Typography>
        <List people={people} />
        <Button sx={{color: 'pink',  bgcolor: '#900C3F'}} variant="contained" onClick={()=>setPeople([])}>Clear All</Button>
        {/* <button onClick={() => setPeople([])}>clear all</button> */}
      </Grid>

    </Grid>

    // <main>
    //   <section className='container'>
    //     <h3>{people.length} birthdays today</h3>
    //     <List people={people} />
    //     <button onClick={() => setPeople([])}>clear all</button>
    //   </section>
    // </main>
  );
}

export default App;
