import { ClassNames } from "@emotion/react";
import { Badge, Card, CardContent, Typography, Box } from "@mui/material";
import React from "react";
import { faChevronLeft, faChevronRight, FaQuoteRight } from "react-icons/fa";

export default function ReviewContent({ id, name, job, image, text }) {
  return (
    <Card>
      <CardContent
        sx={{
          justifyContent: "center",
          alignItems: "center",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <Badge
          badgeContent={
            <FaQuoteRight
              style={{
                backgroundColor: "#49adef",
                padding: "5px",
                borderRadius: "40%",
                color: "#fff",
              }}
            />
          }
          overlap="circular"
          anchorOrigin={{
            vertical: "top",
            horizontal: "left",
          }}
        >
          <Box
            component="img"
            src={image}
            width="100px"
            height="100px"
            sx={{
              borderRadius: "50%",
              objectFit: "cover",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "4px -2px 0px 2px #49a6e9",
            }}
          />
        </Badge>
        <Typography className="fontRoboto" variant="h6" sx={{fontWeight: "bold"}}>{name}</Typography>
        <Typography variant="subtitle1" sx={{color: "#49adef"}}>{job.toUpperCase()}</Typography>
        <Typography variant="body1" color="grey">{text}</Typography>
      </CardContent>
    </Card>
  );
}
