import { ClassNames } from "@emotion/react";
import {
  Badge,
  Card,
  CardContent,
  Typography,
  Box,
  IconButton,
  Button,
} from "@mui/material";
import React from "react";
import { FaChevronLeft, FaChevronRight, FaQuoteRight } from "react-icons/fa";

export default function ReviewContent({ id, name, job, image, text }) {
  return (
    <Card>
      <CardContent
        sx={{
          justifyContent: "center",
          alignItems: "center",
          display: "flex",
          flexDirection: "column",
          maxWidth: "40vw",
            maxHeight: "60vh",
        }}
      >
        <Badge
          badgeContent={
            <FaQuoteRight
              style={{
                backgroundColor: "#49adef",
                padding: "5px",
                borderRadius: "40%",
                color: "#fff",
                maxHeight: "250px",
                maxWidth: "250px",
              }}
            />
          }
          overlap="circular"
          anchorOrigin={{
            vertical: "top",
            horizontal: "left",
          }}
        >
          <Box
            component="img"
            src={image}
            width="150px"
            height="150px"
            sx={{
              borderRadius: "50%",
              objectFit: "cover",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "4px -2px 0px 2px #49a6e9",
            }}
          />
        </Badge>
        <Typography
          className="fontRoboto"
          variant="h6"
          sx={{ fontWeight: "bold" }}
        >
          {name}
        </Typography>
        <Typography variant="subtitle1" sx={{ color: "#49adef" }}>
          {job.toUpperCase()}
        </Typography>
        <Typography variant="body1" color="grey">
          {text}
        </Typography>
        <Box>
          <IconButton>
            <FaChevronLeft color="#8bcbf9"></FaChevronLeft>
          </IconButton>
          <IconButton>
            <FaChevronRight color="#8bcbf9"></FaChevronRight>
          </IconButton>
        </Box>
        <Button
          sx={{
            backgroundColor: "#ebf7ff",
            color: "#49adef",
            textTransform: "none",
            py: 0,
            mt: 1,
            mb: 2,
          }}
        >
          Surprise Me
        </Button>
      </CardContent>
    </Card>
  );
}
