import React, { useState } from "react";
import data from "../data/people.json";
import { Box, Typography, Grid, Divider } from "@mui/material";
import ReviewContent from "./review_content";

export default function Review() {
  const [index, setIndex] = useState(1);
  const [people] = useState(data.reviews);
  console.log(people);
  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        width: "100vw",
        height: "100vh",
      }}
    >
      <Grid
        container
        spacing={3}
        sx={{ justifyContent: "center", alignItems: "center" }}
      >
        <Grid item xs={12}>
          <Typography
            variant="h2"
            component="h2"
            fontWeight="bold"
            sx={{
              color: "black",
              display: "inline",
            }}
          >
            <Typography variant="h2" sx={{ display: "inline" }}>
              Our Reviews
            </Typography>
            <Divider
              sx={{
                borderBottomWidth: 5,
                borderBottomColor: "#49adef",
                mx: 75,
              }}
            />
          </Typography>
        </Grid>
        <Grid item xs={12} sx={{ alignItems: "center" }}>
          <div
            style={{
              alignItems: "center",
              justifyContent: "center",
              maxHeight: "60vh",
              maxWidth: "40vw",
              margin: "auto",
            }}
          >
            <ReviewContent {...people[index]} />
          </div>
        </Grid>
      </Grid>
    </Box>
  );
}
