import React, { useState } from "react";
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Grid,
  CardActions,
  Button,
} from "@mui/material";

export default function TourContent({ id, image, info, name, price }) {
  const [readmore, setReadMore] = useState(false);

  return (
    <Card sx={{ borderRadius: 3, boxShadow: "5px 5px 5px 5px #888888", maxWidth: 700, minHeight: 430, mb: 3}}>
      <CardMedia component="img" height="194" image={image} />
      <CardContent>
        <Grid container>
          <Grid item xs={8} alignItems="center">
            <Typography
              variant="h6"
              sx={{ fontWeight: "bold", fontColor: "black" }}
            >
              {name}
            </Typography>
          </Grid>
          <Grid item xs={4}>
            <Box sx={{ boxShadow: "blue" }}>
              <Typography variant="body2" color="text.primary">
                {price}
              </Typography>
            </Box>
          </Grid>
        </Grid>
        <Typography variant="body2" color="text.secondary">
          {info}
        </Typography>
        <Button
          sx={{
            color: "hsl(360, 67%, 44%)",
            width: "60%",
            textTransform: "none",
            fontWeight: "bold",
            fontSize: "1.2rem",
            backgroundColor: "white",
            border: 1,
            marginTop: 1,
            borderColor: "hsl(360, 67%, 44%)",
            "&:hover": {
              backgroundColor: "#f28ab2",
            },
          }}
          variant="contained"
        >
          Not Interested
        </Button>
      </CardContent>
    </Card>
  );
}
