import React, { useState, useEffect } from "react";
import LoadingAnimation from "./components/loading";
import Tours from "./components/tours";
import { Box, Typography, Button } from "@mui/material";
import "./App.css";

function App() {
  const [loading, setLoading] = useState(true);
  const [tours, setTours] = useState([]);

  const url = "https://course-api.com/react-tours-project";

  const removeTour = (id) => {
    const newTours = tours.filter((tour) => tour.id !== id);
    setTours(newTours);
  };

  const fetchTours = async () => {
    setLoading(true);
    try {
      const response = await fetch(url);
      const tours = await response.json();
      setLoading(false);
      setTours(tours);
    } catch (error) {
      setLoading(false);
      console.log(error);
    }
  };

  useEffect(() => {
    fetchTours();
  }, []);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  return loading ? (
    <LoadingAnimation />
  ) : tours.length === 0 ? (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        width: "100vw",
        height: "100vh",
      }}
    >
      <Typography
        variant="h2"
        sx={{
          display: "inline",
          textDecoration: "underline hsl(205, 78%, 60%)",
        }}
      >
        No tours left
      </Typography>
    </Box>
  ) : (
    <Tours tours={tours} removeTour={removeTour} />
  );

  // if (loading) {
  //   return (
  //     <main>
  //       <LoadingAnimation />
  //     </main>
  //   );
  // }
  // else {
  //   return (
  //     <main>
  //       <Tours tours={tours} />
  //     </main>
  //   );
  // }
}

export default App;
