import React, { useState } from "react";
import {
  Typography,
  Card,
  CardContent,
  CardHeader,
  CardMedia,
  Grid,
  CardActions,
  Button,
} from "@mui/material";
import { Box } from "@mui/system";

export default function TourContent({ id, image, info, name, price }) {
  const [readmore, setReadMore] = useState(false);

  return (
    <Card
      sx={{
        boxShadow: "5px 5px 5px 5px #888888",
        maxWidth: 700,
        minHeight: 430,
        mb: 3,
      }}
    >
      <CardMedia component="img" height="194" image={image} />
      <CardContent>
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            flexDirection: "row",
            pr: 2,
          }}
        >
          <Typography gutterBottom fontFamily="Roboto" fontWeight="bold">
            {name}
          </Typography>
          <Typography
            gutterBottom
            fontWeight="bold"
            sx={{
              backgroundColor: "#ebf7ff",
              borderRadius: "5px",
              p: "2px",
              color: "#49adef",
            }}
          >
            {price}
          </Typography>
        </Box>
        <Typography variant="body2" color="text.secondary">
          {info}
        </Typography>
        <Button
          sx={{
            color: "hsl(360, 67%, 44%)",
            width: "60%",
            textTransform: "none",
            fontWeight: "bold",
            fontSize: "1.2rem",
            backgroundColor: "white",
            border: 1,
            marginTop: 2,
            borderColor: "hsl(360, 67%, 44%)",
            "&:hover": {
              backgroundColor: "#f28ab2",
            },
          }}
          variant="contained"
        >
          Not Interested
        </Button>
      </CardContent>
    </Card>
  );
}
